mapper.select:
SELECT
    *
FROM flash_mastery.decks
WHERE deleted_at IS NULL

  AND folder_id = #{folderId}


  AND LOWER(name) LIKE LOWER('%' || #{name} || '%')



ORDER BY ${sortField}

    ${sortDirection}



ORDER BY created_at DESC


LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*) FROM flash_mastery.decks WHERE deleted_at IS NULL
AND folder_id = #{folderId}
AND LOWER(name) LIKE LOWER('%' || #{name} || '%')

mapper.insert:
INSERT INTO flash_mastery.decks (
    id
  , name
  , description
  , card_count
  , type
  , folder_id
  , created_at
  , updated_at
  , deleted_at
) VALUES (
    #{id}
  , #{name}
  , #{description}
  , #{cardCount}
  , #{type}
  , #{folderId}
  , #{createdAt}
  , #{updatedAt}
  , #{deletedAt}
)

mapper.update:
UPDATE flash_mastery.decks
SET name            = #{name}
  , description     = #{description}
  , card_count      = #{cardCount}
  , type            = #{type}
  , folder_id       = #{folderId}
  , updated_at      = #{updatedAt}
WHERE id = #{id}
  AND deleted_at IS NULL
