mapper.select:
SELECT * FROM flash_mastery.flashcards WHERE id IN
#{id}
AND deleted_at IS NULL ORDER BY created_at DESC

mapper.insert:
INSERT INTO flash_mastery.flashcards (
    id
  , deck_id
  , question
  , answer
  , hint
  , type
  , created_at
  , updated_at
  , deleted_at
) VALUES (
    #{id}
  , #{deckId}
  , #{question}
  , #{answer}
  , #{hint}
  , #{type}
  , #{createdAt}
  , #{updatedAt}
  , #{deletedAt}
)

mapper.update:
UPDATE flash_mastery.flashcards
SET deck_id     = #{deckId}
  , question    = #{question}
  , answer      = #{answer}
  , hint        = #{hint}
  , type        = #{type}
  , updated_at  = #{updatedAt}
WHERE id = #{id}
  AND deleted_at IS NULL
